$(document).ready(function(){$(".management button").each(function(){$(this).click(function(t){t.preventDefault()})}),$(".management li input").each(function(){var t="";$(this).focus(function(){inputSelector=$(this),t=inputSelector.val()}),$(this).focusout(function(){var a=$(this).val();$.ajax({method:"POST",url:"includes/assets/updateMail.php",data:{pEmail:t,nEmail:a}}).done(function(a){0==a&&($(".error").html("Invalid mail"),$(inputSelector).val(t))})})}),$(".delete").each(function(){$(this).on("click",function(){var t=$(this).parent(),a=t.find("input").val();$.ajax({method:"POST",url:"includes/assets/deleteMail.php",data:{email:a}}).done(function(){$(t).remove()})})}),$(".addEmail button").on("click",function(){var t=$(".addEmail input").val();$.ajax({method:"POST",url:"includes/assets/addMail.php",data:{email:t}}).done(function(t){0==t?$(".error").html("Invalid mail"):1==t?$(".error").html("Mail already exists"):$(".management ul").html(function(){return $(this).html()+t})})})});